package JavatrainingBasicsPrograms;

public class CountOfOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j=0;
		for(i=0; i<=100; i++) {
			if(i%2==1) {
				//System.out.println("Print" + i);
			j=j+1;
			}
		} System.out.println("Print" + j);

	}

}
